# https://everybody.codes/event/2024/quests/1
note1=""
note2=""
note3=""

danger = {'A':0,'B':1,'C':3, 'D':5, 'x':0}

def solve1(input):
    return input.count('B')+input.count('C')*3


def solve2(input):
    result = 0
    it = iter(list(input))
    for x, y in zip(it, it):
        result += danger[x] + danger[y] + (2 if not (x == 'x' or y == 'x') else 0)
    return result


def solve3(input):
    result = 0
    it = iter(list(input))
    for x, y, z in zip(it, it, it):
        result += danger[x] + danger[y] + danger[z] + (6 if (x+y+z).count('x') == 0 else 2 if (x+y+z).count('x') == 1 else 0)
    return result


assert solve1("ABBAC") == 5
result1 = solve1(note1)

assert solve2("AxBCDDCAxD") == 28
result2 = solve2(note2)

assert solve3("xBxAAABCDxCC") == 30
result3 = solve3(note3)

print(f'Part 1: {result1} Part 2: {result2} Part 3: {result3}')